import React, { useState, useEffect } from 'react';
import { 
  ThemeProvider, 
  createTheme, 
  CssBaseline, 
  AppBar, 
  Toolbar, 
  Typography, 
  Container, 
  Grid, 
  Paper, 
  Box,
  Card,
  CardContent,
  Button,
  Chip
} from '@mui/material';

// Simple Trading Platform Frontend
const darkTheme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#00d4aa',
    },
    secondary: {
      main: '#ff6b6b',
    },
    background: {
      default: '#121212',
      paper: '#1e1e1e',
    },
  },
});

function Dashboard() {
  const [dashboardData, setDashboardData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:5000/api/dashboard')
      .then(response => response.json())
      .then(data => {
        setDashboardData(data);
        setIsLoading(false);
      })
      .catch(error => {
        console.error('Error fetching dashboard data:', error);
        setIsLoading(false);
      });
  }, []);

  if (isLoading) {
    return <Typography>Loading dashboard data...</Typography>;
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Grid container spacing={3}>
        {/* Summary Cards */}
        <Grid item xs={12} md={3}>
          <Card>
            <CardContent>
              <Typography color="textSecondary" gutterBottom>
                Total Strategies
              </Typography>
              <Typography variant="h4" component="div">
                {dashboardData?.summary?.total_strategies || 0}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        
        <Grid item xs={12} md={3}>
          <Card>
            <CardContent>
              <Typography color="textSecondary" gutterBottom>
                Total Backtests
              </Typography>
              <Typography variant="h4" component="div">
                {dashboardData?.summary?.total_backtests || 0}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        
        <Grid item xs={12} md={3}>
          <Card>
            <CardContent>
              <Typography color="textSecondary" gutterBottom>
                Kite API Status
              </Typography>
              <Chip 
                label={dashboardData?.summary?.kite_connected ? "Connected" : "Disconnected"} 
                color={dashboardData?.summary?.kite_connected ? "success" : "error"}
              />
            </CardContent>
          </Card>
        </Grid>
        
        <Grid item xs={12} md={3}>
          <Card>
            <CardContent>
              <Typography color="textSecondary" gutterBottom>
                Platform Status
              </Typography>
              <Chip label="Online" color="success" />
            </CardContent>
          </Card>
        </Grid>

        {/* Recent Backtests */}
        <Grid item xs={12}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Recent Backtests
            </Typography>
            {dashboardData?.recent_backtests?.length > 0 ? (
              <Grid container spacing={2}>
                {dashboardData.recent_backtests.map((backtest, index) => (
                  <Grid item xs={12} md={6} key={index}>
                    <Card variant="outlined">
                      <CardContent>
                        <Typography variant="h6">{backtest.name}</Typography>
                        <Typography color="textSecondary">
                          Symbol: {backtest.symbol}
                        </Typography>
                        <Typography variant="h4" color="primary">
                          {backtest.return_pct ? `${backtest.return_pct.toFixed(2)}%` : 'N/A'}
                        </Typography>
                        <Typography variant="caption">
                          {new Date(backtest.created_at).toLocaleDateString()}
                        </Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            ) : (
              <Typography>No backtests available</Typography>
            )}
          </Paper>
        </Grid>

        {/* Quick Actions */}
        <Grid item xs={12}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Quick Actions
            </Typography>
            <Box sx={{ display: 'flex', gap: 2, flexWrap: 'wrap' }}>
              <Button variant="contained" color="primary">
                Create Strategy
              </Button>
              <Button variant="contained" color="secondary">
                Run Backtest
              </Button>
              <Button variant="outlined">
                View Historical Data
              </Button>
              <Button variant="outlined">
                ML Models
              </Button>
            </Box>
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
}

function App() {
  return (
    <ThemeProvider theme={darkTheme}>
      <CssBaseline />
      <div className="App">
        <AppBar position="static">
          <Toolbar>
            <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
              🚀 Advanced Trading Platform
            </Typography>
            <Typography variant="body2">
              v1.0.0
            </Typography>
          </Toolbar>
        </AppBar>
        
        <Dashboard />
      </div>
    </ThemeProvider>
  );
}

export default App;
